type=$("#extFT1 :radio:checked");if(tftype.length<1)notify("Please indicate the type of transfer you which to initiate");else{loadit();setTimeout(function(){loadstp();if(tftype.val()=="2")goto(2);else goto('','#prepopmoney')},sec)}}function extFT2(){var amount=$("#amount").val(),bal=<?php echo $accbal;?>;if(!amount||amount==0)notify("Please tell us how much you wish to transfer");else if(!$.isNumeric(amount))notify("Amount must contain numbers only");else{$("#amount").val(parseFloat(amount).toFixed(2));loadit();setTimeout(function(){loadstp();if(amount>bal)notify("You have insufficient balance to perform this transfer.\n\rThe balance on your account is:  "+bal);else goto(3)},sec)}}function extFT3(){var f,farr=["amount","country-f","accno-f","bankname","bankaddr","bankaddr2","fname","mname","sname"],farr2=["routing-f","iban-f","swift-f"],name=($("#mname").val()=="")?$("#sname").val():$("#mname").val()+" "+$("#sname").val();$("#name-f").val($("#fname").val()+' '+name);$("#extFT3 :text,#extFT3 select").css("border","solid 1px #999").not(".optional").each(function(){if($(this).val()==""){$(this).css("border","solid 2px #f00");f=true}});if(f)notify("Please complete all fields marked in red");else if(!$.isNumeric($("#accno-f").val())){notify("Account number must contain numbers only")}else{loadit();setTimeout(function(){loadstp();$.each(farr,function(indx,val){$("#r-"+val).text($("#"+val).val())});$.each(farr2,function(indx,val){if($("#"+val).val()!=""){$("#r-"+val).text($("#"+val).val());$("#tr-"+val).show()}else $("#tr-"+val).hide()});goto(4)},sec+1000)}}function extFT4(){loadit();setTimeout(function(){loadstp();$("#tform").submit();},sec+sec)}</script>

